import { useEffect, useRef, useState } from "react";
import "./App.css";

import Modal from "./components/Modal";

import "bootstrap/dist/css/bootstrap.min.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCancel,
  faCircleInfo,
  faEdit,
  faInfo,
  faInfoCircle,
  faSave,
  faTrashAlt,
  faUpload,
} from "@fortawesome/free-solid-svg-icons";
import { text } from "@fortawesome/fontawesome-svg-core";

function App() {
  const [isLoading, setIsLoading] = useState(true);

  const [producto, setProducto] = useState(null);
  const [showModalInfo, setShowModalInfo] = useState(false);
  const [showModalInsert, setShowModalInsert] = useState(false);
  const [showModalUpdate, setShowModalUpdate] = useState(false);
  const [showModalDelete, setShowModalDelete] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const saveProduct=(e,action)=>{
    e.preventDefault()
    const data=Object.fromEntries(new FormData(e.target));
    console.log(data);
    data.estado=1
    fetch('http://api.com/producto',{
      method:action,
      body:JSON.stringify(data)
    }).then(res=>res.text()).then(data=>{

    console.log(data)
    fetchProducts()
    })
  }


  const openInfo = () => {
    setShowModalInfo(true);
  };

  const openInsert = () => {
    setShowModalInsert(true);
  };
  const closeInsert = () => {
    setShowModalInsert(false);
  };
  const openUpdate = () => {
    setShowModalUpdate(true);
  };
  const closeUpdate = () => {
    setShowModalUpdate(false);
  };
  const openDelete = () => {
    showModalDelete(true);
  };
  const closeDelete = () => {
    showModalDelete(false);
  };

  const selectProduct = (id,op) => {
    setSelectedProduct(producto.find((p) => p.idpro === id));
    switch (op) {
      case 'INFO':
        openInfo();
        break;
      case 'UPDATE':
        openUpdate();
        break;
    }

  };

  const fetchProducts=()=>{
    fetch("http://api.com/producto")
    .then((response) => response.json())
    .then((data) => {
      setProducto(data.producto);

      setIsLoading(false);
    });
  }

  useEffect(() => {
    fetchProducts()
  }, []);



  return (
    <div className="App">
      {showModalInfo ? (
        <Modal
          closeModal={() => {
            setShowModalInfo(false);
          }}
          modalTitle={selectedProduct.categoria + "/" + selectedProduct.nombre}
          modalBody={
            <>
              <img src={selectedProduct.imagen} width="200" height="200"></img>
              <h2>$ {selectedProduct.precio}</h2>
              <p>{selectedProduct.descripcion}</p>
            </>
          }
          modalFooter={
            <>
              <p>ID: {selectedProduct.idpro}</p>
              <p>Stock: {selectedProduct.stock}</p>
            </>
          }
        />
      ) : (
        ""
      )}

      {showModalInsert ? (
        <form onSubmit={(e)=>saveProduct(e,'POST')}>
        <Modal
          closeModal={() => {
            setShowModalInsert(false);
          }}
          modalTitle={
            <>
              <p>New Product</p>
            </>
          }
          modalBody={
            <>

                <p>
                  Producto: <input type="text" name="nombre"/>
                </p>
                <p>
                  Categoria:
                  <select name="idcat" >
                  {producto.map(p=>(
                      <option key={p.idpro} value={p.idpro}>{p.nombre}</option>
                    ))}
                  </select>
                </p>
                <p>
                  Precio: <input type="number" name="precio"/>
                </p>
                <p>
                  Stock: <input type="number" name="stock"/>
                </p>
                <p>
                  URL Imagen: <input type="text" name="imagen" />
                </p>
                <p>
                  Descripcion: <textarea className="textarea-form" name="descripcion"/>
                </p>

            </>
          }
          modalFooter={
            <>
              <p>
                <button className="btn btn-success" >
                  <FontAwesomeIcon icon={faSave} /> Save
                </button>
              </p>
              <p>
                <button className="btn btn-danger" type="button" onClick={closeInsert}>
                  <FontAwesomeIcon icon={faCancel} /> Cancel
                </button>
              </p>
            </>
          }
        />
        </form>
      ) : (
        ""
      )}

      {showModalUpdate ? (
        <Modal
          closeModal={() => {
            setShowModalUpdate(false);
          }}
          modalTitle={
            <>
              <p>Update Product</p>
            </>
          }
          modalBody={
            <>
              <form>
                <p>
                  Producto: 
                  <input type="text" value={selectedProduct.nombre}></input>
                </p>
                <p>
                  Categoria:
                  <select>
                    {producto.map(p=>(
                      <option key={p.idpro} value={p.idpro}>{p.nombre}</option>
                    ))}
                  </select>
                </p>
                <p>
                  Precio: <input type="number" />
                </p>
                <p>
                  Stock: <input type="number" />
                </p>
                <p>
                  URL Imagen: <input type="text" />
                </p>
                <p>
                  Descripcion: <textarea className="textarea-form" />
                </p>
              </form>
            </>
          }
          modalFooter={
            <>
              <p>
                <button className="btn btn-success">
                  <FontAwesomeIcon icon={faUpload} /> Update
                </button>
              </p>
              <p>
                <button className="btn btn-danger" onClick={closeUpdate}>
                  <FontAwesomeIcon icon={faCancel} /> Cancel
                </button>
              </p>
            </>
          }
        />
      ) : (
        ""
      )}

      <header className="App-header">
        <button className="btn btn-success" onClick={openInsert}>
          Nuevo
        </button>

        <div className="container-characters">
          {!producto ? (
            <>
              <h1></h1>
            </>
          ) : (
            producto.map((producto) => {
              return (
                <div className="character-container" key={producto.idpro}>
                  <img src={producto.imagen} width="350" height="350"></img>
                  <h6>{producto.nombre}</h6>
                  <h6>$ {producto.precio}</h6>
                  <button
                    className="btn btn-success"
                  >
                    <FontAwesomeIcon icon={faEdit} /> Edit
                  </button>
                  <span> </span>
                  <button className="btn btn-danger">
                    <FontAwesomeIcon icon={faTrashAlt} /> Delete
                  </button>
                  <span> </span>
                  <button
                    className="btn btn-primary"
                    onClick={() => selectProduct(producto.idpro)}
                  >
                    <FontAwesomeIcon icon={faInfoCircle} /> Detail
                  </button>
                </div>
              );
            })
          )}
        </div>
        <p></p>
      </header>
    </div>
  );
}

export default App;
