import { useEffect, useState } from "react";
import './App.css';


import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';



 function App() {

  const [isLoading, setIsLoading] = useState(true);
  const [producto, setProducto] = useState(null);
  const [showModal, setShowModal] = useState(false);

  const handleClose = () => {
    setShowModal(false)
  };
  const handleShow = () => {setShowModal(true)
    console.log({showModal});
  }

  useEffect(() => {
    fetch("http://api.com/producto")
      .then((response) => response.json())
      .then((data) => {
        setProducto(data.producto)

        setIsLoading(false);
      });
  }, []);

  return (
    <div className="App">

      <header className="App-header">
        <button className="btn btn-success">Nuevo</button>

        {showModal? <div class="modal" >
  <div class="modal-dialog" role="document">
    <div class="modal-content">
      <div class="modal-header">
        <h5 class="modal-title">Modal title</h5>
        <button type="button" class="close" data-dismiss="modal" aria-label="Close">
          <span aria-hidden="true">&times;</span>
        </button>
      </div>
      <div class="modal-body">
        <p>Modal body text goes here.</p>
      </div>
      <div class="modal-footer">
        <button type="button" class="btn btn-primary">Save changes</button>
        <button type="button" class="btn btn-secondary" data-dismiss="modal">Close</button>
      </div>
    </div>
  </div>
</div>:<div></div>}
        <div className="container-characters">
          {!producto ? (
          <>
          <h1></h1>
          </>)
          : producto.map((producto) => {
          return (
            <div className="character-container" key={producto.idpro}>
              <img src={producto.imagen} width="350" height="350"></img>
              <h6>{producto.nombre}</h6>
              <h6>$ {producto.precio}</h6>
              <button className="btn btn-success" onClick={handleShow}><FontAwesomeIcon icon={faEdit}/> Edit</button>
              <span> </span>
              <button variant="primary" onClick={handleShow}>
                Launch demo modal
                </button>
              <button className="btn btn-danger"><FontAwesomeIcon icon={faTrashAlt}/> Delete</button>
            </div>
          );
        })}
        </div>

      </header>
    </div>
  );
}

export default App;
