import { useEffect, useState } from "react";
import './App.css';
import "bootstrap/dist/css/bootstrap.min.css";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome';
import { faEdit, faTrashAlt } from '@fortawesome/free-solid-svg-icons';
import { Modal, ModalBody, ModalFooter, ModalHeader } from 'reactstrap';


 function App() {

  const [isLoading, setIsLoading] = useState(true);
  const [producto, setProducto] = useState(null);
  const [showModal, setShowModal] = useState(null);

  const openModal=()=>{
    setShowModal(true)
  }

  useEffect(() => {
    fetch("http://api.com/producto")
      .then((response) => response.json())
      .then((data) => {
        setProducto(data.producto)

        setIsLoading(false);
      });
  }, []);

  return (
    <div className="App">
      {showModal ? 
        <div className="modal">
          <input placeholder="NOMBRE"/>
          <input type="button" value="enviar"/>
          </div>:<div></div>}

      <header className="App-header">
        <button className="btn btn-success">Nuevo</button>
        <div className="container-characters">
          {!producto ? (
          <>
          <h1>Cargando...</h1>
          </>)
          : producto.map((producto) => {
          return (
            <div className="character-container" key={producto.idpro}>
              <img src={producto.imagen} width="350" height="350"></img>
              <h6>{producto.nombre}</h6>
              <h6>$ {producto.precio}</h6>
              <button className="btn btn-success" onClick={openModal}><FontAwesomeIcon icon={faEdit}/> Edit</button>
              <span> </span>
              <button className="btn btn-danger"><FontAwesomeIcon icon={faTrashAlt}/> Delete</button>
              <Modal show={showModal}>
                <ModalBody>
                  Estás seguro que deseas eliminar a la empresa
               </ModalBody>
              </Modal>
            </div>
          );
        })}
        </div>
      </header>
    </div>
  );
}

export default App;
