import { useEffect, useState } from "react";
import "./App.css";

import "bootstrap/dist/css/bootstrap.min.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import {
  faCircleInfo,
  faEdit,
  faInfo,
  faInfoCircle,
  faTrashAlt,
} from "@fortawesome/free-solid-svg-icons";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [producto, setProducto] = useState(null);
  const [showModalInfo, setShowModalInfo] = useState(false);
  const [showModalInsert, setShowModalInsert] = useState(false);
  const [selectedProduct, setSelectedProduct] = useState(null);

  const closeInfo = () => {
    setShowModalInfo(false);
  };
  const openInfo = () => {
    setShowModalInfo(true);
    console.log({ showModalInfo });
  };

  const closeInsert = () => {
    setShowModalInsert(false);
  };
  const openInsert = () => {
    setShowModalInsert(true);
    console.log({ showModalInsert });
  };

  const selectProduct = (id) => {
    setSelectedProduct(producto.find((p) => p.idpro === id));
    openInfo();
  };

  useEffect(() => {
    fetch("http://api.com/producto")
      .then((response) => response.json())
      .then((data) => {
        setProducto(data.producto);

        setIsLoading(false);
      });
  }, []);

  return (
    <div className="App">
      {showModalInfo ? (
        <div
          className="modal fade show"
          style={{ display: "block", background: "#000000dd" }}
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">
                  {selectedProduct.categoria} / {selectedProduct.nombre}
                </h5>
                <button type="button" onClick={closeInfo} className="close">
                <span aria-hidden="true">&X;</span>
                </button>
              </div>
              <div className="modal-body">
                <img
                  src={selectedProduct.imagen}
                  width="200"
                  height="200"
                ></img>
                <h2>$ {selectedProduct.precio}</h2>

                <p>{selectedProduct.descripcion}</p>
              </div>
              <div className="modal-footer">
                <p>ID: {selectedProduct.idpro}</p>
                <p>Stock: {selectedProduct.stock}</p>
              </div>
            </div>
          </div>
        </div>
      ) : (
        ""
      )}

      {showModalInsert ? (
        <div
        className="modal fade show"
        style={{ display: "block", background: "#000000dd" }}
      >
        <div className="modal-dialog" role="document">
          <div className="modal-content">
            <div className="modal-header">
              <button type="button" onClick={closeInfo} className="close">
                <span aria-hidden="true">&times;</span>
                <p>New Product</p>
              </button>
            </div>
            <div className="modal-body">
            </div>
            <div className="modal-footer">
            </div>
          </div>
        </div>
      </div>
      )
      :("")}

      <header className="App-header">
        <button  className="btn btn-success">Nuevo</button>

        <div className="container-characters">
          {!producto ? (
            <>
              <h1></h1>
            </>
          ) : (
            producto.map((producto) => {
              return (
                <div className="character-container" key={producto.idpro}>
                  <img src={producto.imagen} width="350" height="350"></img>
                  <h6>{producto.nombre}</h6>
                  <h6>$ {producto.precio}</h6>
                  <button className="btn btn-success" onClick={() => selectProduct(producto.idpro)}>
                    <FontAwesomeIcon icon={faEdit} /> Edit
                  </button>
                  <span> </span>
                  <button className="btn btn-danger">
                    <FontAwesomeIcon icon={faTrashAlt} /> Delete
                  </button>
                  <span> </span>
                  <button
                    className="btn btn-primary"
                    onClick={() => selectProduct(producto.idpro)}
                  >
                    <FontAwesomeIcon icon={faInfoCircle} /> Detail
                  </button>
                </div>
              );
            })
          )}
        </div>
        <p></p>
      </header>
    </div>
  );
}

export default App;
