import { useEffect, useState } from "react";
import "./App.css";

import "bootstrap/dist/css/bootstrap.min.css";

import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faEdit, faTrashAlt } from "@fortawesome/free-solid-svg-icons";

function App() {
  const [isLoading, setIsLoading] = useState(true);
  const [producto, setProducto] = useState(null);
  const [showModal, setShowModal] = useState(false);
  const [selectedProduct, setSelectedPrduct] = useState(null);

  const handleClose = () => {
    setShowModal(false);
  };
  const handleShow = () => {
    setShowModal(true);
    console.log({ showModal });
  };

  const selectProduct = (id) => {
    setSelectedPrduct(producto.find((p) => p.idpro == id));
    handleShow()
  };

  useEffect(() => {
    fetch("http://api.com/producto")
      .then((response) => response.json())
      .then((data) => {
        setProducto(data.producto);

        setIsLoading(false);
      });
  }, []);

  /*const postPro = () => {
    fetch("http://api.com/producto",{
      method : "POST",
      body : JSON.stringify()
    })
  }*/

  return (
    <div className="App">
      {showModal ? (
        <div
          className="modal fade show"
          style={{ display: "block", background: "#000000dd" }}
        >
          <div className="modal-dialog" role="document">
            <div className="modal-content">
              <div className="modal-header">
                <h5 className="modal-title">Modal title</h5>
                <button
                  type="button"
                  onClick={handleClose}
                  className="close"
                >
                  <span aria-hidden="true">&times;</span>
                </button>
              </div>
              <div className="modal-body">
              {selectedProduct.nombre}

              </div>
              <div className="modal-footer">
                <button type="button" className="btn btn-primary" >
                  Save changes
                </button>
                <button
                  type="button"
                  onClick={handleClose}
                  className="btn btn-secondary"
                >
                  Close
                </button>
              </div>
            </div>
          </div>
        </div>
      ) : ""}

      <header className="App-header">
        <button className="btn btn-success">Nuevo</button>

        <div className="container-characters">
          {!producto ? (
            <>
              <h1></h1>
            </>
          ) : (
            producto.map((producto) => {
              return (
                <div className="character-container" key={producto.idpro}>
                  <img src={producto.imagen} width="350" height="350"></img>
                  <h6>{producto.nombre}</h6>
                  <h6>$ {producto.precio}</h6>
                  <button
                    className="btn btn-success"
                    onClick={ ()=>selectProduct(producto.idpro)}
                  >
                    <FontAwesomeIcon icon={faEdit} /> Edit
                  </button>
                  <span> </span>
                  <button className="btn btn-danger">
                    <FontAwesomeIcon icon={faTrashAlt} /> Delete
                  </button>
                </div>
              );
            })
          )}
        </div>
      </header>
    </div>
  );
}

export default App;
